package br.com.senaisp.richard.licao01;

import java.util.Scanner;

public class GalToLitros2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scn = new Scanner(System.in);
		//Definir as variaveis que vou precisar
		int galoes = 0;
		float litros = 0;
		float litrosPorGalao = 3.785f;
		//Interação com o usuário
		//fazer com que peça via teclado um numero inteiro
		System.out.println("Digite a quantidade de galões:");
		galoes = scn.nextInt();
		scn.close();
		//calculando
		litros = galoes * litrosPorGalao;
		System.out.println("O Resultado é " + litros);
	}

}
